mapper.select:
SELECT COUNT(1)
  FROM tb_sys_user
 WHERE use_yn = 'Y'
    AND hotel_cd = #{hotelCd} 
    AND user_id = #{userId} 
    AND emp_no = #{empNo} 
    AND user_nm = #{userNm} 
    AND user_nm_en = #{userNmEn}

mapper.update:
UPDATE tb_sys_user
   SET use_yn = 'N'
 WHERE user_seq=#{userSeq}
